@import "../sass/mixins.scss";

//MAIN//

header {
  @include pink-bg;
  width: 100%;
  height: 80px;
  z-index: 100;
  position: fixed;
  top: 0;

  .main-header {
    width: 100%;
    height: 100%;
    justify-content: space-between;
    align-items: center;
    padding: 10px;

    .logo {
      padding: 5px;
      @include dark-title;
      font-size: 24px;
    }
    .nav {
      nav {
        ul {
          display: flex;
          align-items: center;

          li {
            display: block;
            transition-duration: 0.5s;
            padding: 8px;
            align-items: center;
            border-radius: 10px;

            a {
              text-decoration: none;
              @include dark-para;
              font-weight: bold;

              svg {
                height: auto;
                width: 30px;
              }
            }
          }

          li:hover {
            cursor: pointer;
            @include blue-bg;
          }

          li ul {
            visibility: hidden;
            opacity: 0;
            position: absolute;
            transition: all 0.5s ease;
            @include blue-bg;
            display: none;
            margin-top: 10px;
            padding: 0px 8px 8px 8px;
            border-radius: 10%;
          }

          li:hover > ul,
          li ul:hover {
            visibility: visible;
            opacity: 1;
            display: block;
            @include pink-bg;
          }

          li ul li {
            clear: both;
            width: 100%;

            display: block;

            a {
              text-decoration: underline;
            }
          }
        }
      }
    }
  }
}

@media only screen and (max-width: 420px) {
  header {
    @include pink-bg;
    height: 120px;

    .main-header {
      display: flex;
      flex-wrap: wrap;
      justify-content: center;

      .logo {
        font-size: 28px;
      }
    }
  }
}

