@import "./mixins.scss";

.banner {
  width: 100%;
  margin-top: 90px;

  .header-banner {
    width: 100%;
    display: flex;
    justify-content: center;

    .color {
      width: 100%;
      display: flex;
      background-color: #cceaff;
      justify-content: center;
      border-radius: 93% 7% 90% 10% / 22% 79% 21% 78%;
    }

    .hb-img {
      margin: 20px 0 30px 0;
      display: flex;
      width: 90%;
      height: 600px;
      background-image: url(../img/pink-living-room-chair.jpg);
      background-repeat: no-repeat;
      background-position: cover;
      border-radius: 50px;
      align-items: center;
      justify-content: center;

      .text {
        display: flex;
        //background-color: #ffffff63;

        p {
          @include light-title;
          font-size: 50px;
          font-style: italic;
        }
      }
    }
  }
}

.showrooms {
  margin: 10px 0;
  width: 100%;

  .sr-cards {
    display: flex;
    flex-wrap: wrap;

    .sr-card {
      width: 100%;
      height: 400px;
      display: flex;
      justify-content: space-between;
      margin: 10px 0;
      padding: 8px;

      .left {
        border-radius: 100% 0% 100% 0% / 54% 52% 48% 46%;
        background-color: #ffdecc;
      }

      .right {
        border-radius: 0% 100% 0% 100% / 46% 48% 52% 54%;
        background-color: #cceaff;
      }

      .color {
        width: 400px;
        display: flex;
        justify-content: center;

        .sr-img {
          width: 80%;
          height: 100%;
          img {
            width: 100%;
            height: 100%;
            border-radius: 50px;
          }
        }
      }

      .sr-info {
        width: 800px;
        display: flex;
        align-items: center;
        justify-content: center;
        p {
          width: 80%;
          border-radius: 20px;
          background-color: rgb(238, 232, 232);
          line-height: 2;
          padding: 15px;
          text-align: center;
          @include dark-para;
        }
      }
    }
  }
}

.home-shop {
  margin: 10px 0;
  width: 100%;

  .hs-cards {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    height: auto;

    .hs-card {
      width: 33%;
      height: 400px;
      display: flex;
      flex-wrap: wrap;
      margin: 10px auto;
      padding: 8px;

      .left {
        border-radius: 100% 0% 100% 0% / 54% 52% 48% 46%;
        background-color: #ffdecc;
      }

      .right {
        border-radius: 0% 100% 0% 100% / 46% 48% 52% 54%;
        background-color: #cceaff;
      }

      .color {
        width: 70%;
        height: 60%;
        display: flex;
        justify-content: center;
        margin: 0 auto;

        .hs-img {
          width: 90%;
          height: 100%;
          img {
            width: 100%;
            height: 100%;
            border-radius: 50px;
          }
        }
      }

      .hs-info {
        width: 100%;
        display: flex;
        align-items: center;
        justify-content: center;

        p {
          width: 80%;
          border-radius: 20px;
          background-color: rgb(238, 232, 232);
          line-height: 2;
          padding: 10px;
          @include dark-para;
          text-align: center;
        }
      }
    }
  }
}

//MEDIA//

@media only screen and (max-width: 420px) {
  .banner {
    width: 100%;
    margin-top: 120px;

    .header-banner {
      width: 100%;
      display: flex;
      justify-content: center;

      .color {
        background-color: #cceaff;
        border-radius: 0;
        padding: 5px 0;
      }

      .hb-img {
        margin: 5px;
        width: 100%;
      }
    }
  }

  .showrooms {
    margin: 10px 0;
    width: 100%;

    .sr-cards {
      display: block;
      height: auto;

      .sr-card {
        height: auto;
        flex-wrap: wrap;
        justify-content: center;
        padding: 0 8px;

        .color {
          width: 300px;
          margin: 10px 0;
          height: 280px;

          .sr-img {
            width: 90%;
            height: 100%;
          }
        }

        .sr-info {
          p {
            width: 100%;
          }
        }
      }
    }
  }

  .home-shop {
    margin: 10px 0;
    width: 100%;
    height: auto;
    .hs-cards {
      .hs-card {
        width: 100%;
        height: 200px;

        .color {
          width: 50%;
          height: 100%;

          .hs-img {
            width: 90%;
            height: 100%;
          }
        }

        .hs-info {
          width: 50%;

          p {
            width: 90%;
          }
        }
      }
    }
  }
}

