@import "./mixins.scss";

//MAIN//

footer {
  width: 100%;

  .footer {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    height: auto;
    background-color: #cceaff;
    border-radius: 20px 20px 0 0;
    border-radius: 93% 7% 100% 0% / 22% 100% 0% 100%;

    .map {
      display: flex;
      width: 100%;
      height: auto;
      justify-content: center;
      border-radius: 20px 20px 0 0;
      padding: 10px;
      margin-top: 10px;

      img {
        width: 80%;
        height: auto;
        border-radius: 20px;
      }
    }
    .f-info {
      display: flex;
      width: 100%;
      height: 150px;
      background-color: #cceaff;
      padding-top: 10px;
      flex-wrap: wrap;

      .footer-nav {
        display: flex;
        height: 70px;
        width: 100%;
        margin: 0 auto;

        nav {
          margin: 0 auto;

          ul {
            height: 100%;

            display: flex;
            align-items: center;

            li {
              height: 100%;
              display: flex;
              align-items: center;
              margin: 0 auto;

              a {
                text-decoration: none;
                @include dark-para;
                font-weight: bold;
                padding: 8px;
                border-radius: 10px;
                transition-duration: 0.5s;
              }
            }

            a:hover {
              cursor: pointer;
              @include pink-bg;
            }
          }
        }
      }

      .icons {
        height: 70px;
        width: 100%;
        display: flex;
        justify-content: center;

        p {
          height: 100%;
          width: 60px;
          display: flex;
          justify-content: center;

          a {
            padding: 5px;
            height: 50px;
            border-radius: 10px;
            transition-duration: 0.5s;

            svg {
              height: 40px;
              width: auto;
            }
          }
          a:hover {
            cursor: pointer;
            @include pink-bg;
          }
        }
      }
    }
  }
}

//MEDIA//

@media only screen and (max-width: 420px) {
  footer {
    width: 100%;

    .footer {
      .map {
        width: 100%;
        padding-bottom: 0;

        img {
          width: 100%;
        }
      }
      .f-info {
        height: auto;
        padding-bottom: 10px;
        margin-bottom: 10px;

        .footer-nav {
          height: 60px;

          nav {
            margin: 0 auto;
          }
        }

        .icons {
          height: 60px;
        }
      }
    }
  }
}

